import styled from 'styled-components';

export const Container = styled.div`
  background: #232129;
  border-radius: 10px;
  border: 2px solid #232129;
  color: #f4ede8;
  padding: 16px;
  width: 100%;

  display: flex;
  align-items: center;

  input {
    flex: 1;
    border: 0;
    background: transparent;
    &::placeholder {
      color: #666360;
    }

    & + input {
      margin-top: 8px;
    }
  }
`;
